.time-capsule {
  max-width: 340px;
  margin: 2em auto;
  border-radius: 10px;
  overflow: hidden;
  background:rgb(36, 56, 84);
  border: 1px solid #30363d;
}
.capsule-header {
  padding: 14px 16px;
  text-align: center;
  border-bottom: 1px solid #30363d;
}
.capsule-header h3 {
  margin: 0;
  color: #c9d1d9;
  font-size: 0.88em;
  font-weight: 500;
}
.capsule-header .subtitle {
  color: #484f58;
  font-size: 0.7em;
  margin-top: 3px;
}
.capsule-body { padding: 14px 16px; }
.countdown-display {
  text-align: center;
  padding: 10px;
  margin-bottom: 12px;
  background: #0d1117;
  border-radius: 6px;
  border: 1px solid #30363d;
}
.countdown-display .label {
  color: #484f58;
  font-size: 0.68em;
  margin-bottom: 4px;
}
.countdown-display .time {
  color: #8b949e;
  font-size: 0.9em;
  font-weight: 500;
}
.capsule-textarea {
  width: 100%;
  min-height: 70px;
  padding: 10px;
  border: 1px solid #30363d;
  border-radius: 6px;
  background: #0d1117;
  color:rgb(78, 100, 123);
  font-size: 0.8em;
  line-height: 1.5;
  resize: none;
  font-family: inherit;
}
.capsule-textarea:focus {
  outline: none;
  border-color: #58a6ff;
}
.capsule-textarea::placeholder {
  color: #484f58;
  font-size: 0.95em;
}
.capsule-actions {
  display: flex;
  gap: 8px;
  margin-top: 10px;
}
.capsule-btn {
  flex: 1;
  padding: 7px 12px;
  border: none;
  border-radius: 6px;
  font-size: 0.75em;
  cursor: pointer;
  transition: all 0.15s;
  font-family: inherit;
}
.capsule-btn.primary {
  background:rgb(96, 154, 180);
  color: #fff;
}
.capsule-btn.primary:hover { background: #2ea043; }
.capsule-btn.secondary {
  background: #21262d;
  color: #8b949e;
  border: 1px solid #30363d;
}
.capsule-btn.secondary:hover {
  background: #30363d;
  color: #c9d1d9;
}
.capsule-status {
  text-align: center;
  padding: 6px;
  margin-top: 8px;
  border-radius: 6px;
  font-size: 0.72em;
  display: none;
}
.capsule-status.success {
  background: rgba(35,134,54,0.15);
  color:rgb(112, 181, 204);
  display: block;
}
.capsule-status.info {
  background: #21262d;
  color: #8b949e;
  display: block;
}
.saved-message {
  background: #0d1117;
  border: 1px solid #30363d;
  border-radius: 6px;
  padding: 10px;
  margin-top: 10px;
  display: none;
}
.saved-message.show { display: block; }
.saved-message .saved-label {
  color: #484f58;
  font-size: 0.68em;
  margin-bottom: 6px;
}
.saved-message .saved-content {
  color: #8b949e;
  font-size: 0.78em;
  line-height: 1.5;
  white-space: pre-wrap;
}
.saved-message .saved-date {
  color: #484f58;
  font-size: 0.65em;
  margin-top: 6px;
  text-align: right;
}




